mapper.select:
select tb_user_word_mark.uid, tb_user_word_mark.user_phone, tb_user_word_mark.word_id, tb_user_word_mark.word_create_time, tb_user_word_mark.create_time, tb_user_word_mark.update_time, tb_user_word_mark.mark_count, tb_user_word_mark.learn_count, tb_word.content_en, tb_word.content_cn from tb_user_word_mark inner join tb_word on tb_word.uid = tb_user_word_mark.word_id where tb_user_word_mark.user_phone = #{userPhone} and tb_user_word_mark.mark_count > 0
and year(tb_user_word_mark.word_create_time) = #{year} and month(tb_user_word_mark.word_create_time) = #{month}
order by tb_user_word_mark.mark_count desc

mapper.update:
update tb_user_word_mark
set mark_count  = #{markCount},
    learn_count = #{learnCount}
where uid = #{id}

update tb_word_learn_record
set count = #{count}
where id = #{id}
